import React, { Component } from 'react';

export default class Login extends Component {
constructor(props) {
super(props);
this.state = {
    username : '',
    password : ''
}
// handle initialization activities
}
handleChangeEvents(e) {
//handle change events
console.log("handleChangeEvents => ");
const name = e.target.name;
  const value = e.target.value;
  this.setState({username: value});
}
handleSubmitevents(e) {
// handle submit events
console.log("handleSubmitevents => ");
}
handlePasswordChange(e){
//handle password change events
console.log("handlePasswordChange => ");
const name = e.target.name;
  const value = e.target.value;
  console.log(name)
  this.setState({password: value});
}
render() {
return (
<div className=" TestLoginForm ">
<form onSubmit={this.handleSubmitevents}>
{
//handle error condition
}
<label>User Name</label>
<input type="text" data-test="username" value={this.state.username} onChange={(e) => this.handleChangeEvents(e)} />
<label>Password</label>
<input type="password" data-test="password" value={this.state.password} onChange={(e) => this.handlePasswordChange(e) } />
<input type="submit" value="Log In" data-test="submit" />
</form>
</div>
);
}
}
