import React, { Component } from 'react';
import { FormErrors }  from './Components/FormErrors'
export class Login extends Component {
constructor(props) {
super(props);
this.state = {
    username : '',
    password : '',
    formErrors: {username: '', password: ''},
    emailValid: false,
    passwordValid: false,
    formValid: false
}
// handle initialization activities
}
handleChangeEvents(e) {
//handle change events
console.log("handleChangeEvents => ");
const name = e.target.name;
  const value = e.target.value;
  this.setState({[name]: value});
}
handleSubmitevents(e) {
// handle submit events
console.log("handleSubmitevents => ");
}



validateField(fieldName, value) {
  let fieldValidationErrors = this.state.formErrors;
  let emailValid = this.state.emailValid;
  let passwordValid = this.state.passwordValid;
switch(fieldName) {
    case 'email':
      emailValid = value.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i);
      fieldValidationErrors.email = emailValid ? '' : ' is invalid';
      break;
    case 'password':
      passwordValid = value.length >= 6;
      fieldValidationErrors.password = passwordValid ? '': ' is too short';
      break;
    default:
      break;
  }
  this.setState({formErrors: fieldValidationErrors,
                  emailValid: emailValid,
                  passwordValid: passwordValid
                }, this.validateForm);
}
validateForm() {
  this.setState({formValid: this.state.emailValid &&
                            this.state.passwordValid});
}

errorClass(error) {
   return(error.length === 0 ? '' : 'has-error');
}
render() {
return (
<div className=" TestLoginForm ">
<div className="panel panel-default">
 <FormErrors formErrors={this.state.formErrors} />
</div>
<form onSubmit={this.handleSubmitevents}>
{
//handle error condition
}
<label>User Name</label>
<input type="text" data-test="username" value={this.state.username} onChange={(e) => this.handleChangeEvents(e)} />
<div className={`form-group
                 ${this.errorClass(this.state.formErrors.email)}`}></div>
<label>Password</label>
<input type="password" data-test="password" value={this.state.password} onChange={(e) => this.handleChangeEvents(e) } />
<input type="submit" value="Log In" data-test="submit" className="btn btn-primary"
  disabled={!this.state.formValid}/>
</form>
</div>
);
}
}
